arguments.argument:
// Changes the attack mode.


x = attackMode;
while (x == attackMode)
{
    x = irandom_range(1, 4); // Ensures a single attack isn't repeating twice in a row.
}

attackStarted = false;
attackMode = x;

if (hp > 333)
    alarm[0] = irandom_range(300, 600);
else
    alarm[0] = irandom_range(300 - attackSpeedIncrease, 600 - attackSpeedIncrease); // At low health attacks change every 5 - 10 seconds.
