arguments.argument:
if (attackMode != 1)
{
    isShooting = false;
    hasMoved = false;
    spinSpeed = 0;
    exit;
}

if !instance_exists(MainApple) 
    exit;

if (!hasMoved) 
{
    destX = MainApple.x;
    destY = 730;                    
    hasMoved = true; // Prevents roaster from constantly following App like an idiot.
}

move_towards_point(destX, destY, 4 + attackSpeedIncrease);

if (!isShooting)
{
    alarm[2] = 5;
    isShooting = true;
}

while (attackStarted && nextSpin > 1)
{
    image_angle += spinSpeed;
    nextSpin -= 1;

    if (spinSpeed < 25)
        spinSpeed += 0.1;

    if nextSpin == 2
        hasMoved = false;                        
}   

if (nextSpin == 1)
{   
    hasMoved = false;
    nextSpin = 9000 - attackSpeedIncrease;
    alarm[1] = 1;
}
