arguments.argument:
lavaColor = make_color_rgb(176, 27, 0);

if (!variable_local_exists("facing"))
    facing = "Right";

if (!variable_local_exists("chaseSpeed"))
    chaseSpeed = 3;

// Creation Code can set the direction to:
// Right, Left, Up, Down.

switch(facing)
{
    case ("Right"):
            y = room_height;
            hspeed = chaseSpeed;        
            break;

    case ("Left"):
            y = room_height; 
            hspeed = -chaseSpeed;          
            break;                      

    case ("Up"):
            x = 0; 
            vspeed = -chaseSpeed; 
            break;

    case ("Down"):
            x = 0;
            vspeed = chaseSpeed;
            break;
}
