arguments.argument:
if (instance_number(MainApple) = 1)
{
    switch (facing)
    {
        case ("Right"):
            if (MainApple.x < x)
            {
                lives = 0;
                MainApple.alarm[0]=30;
                scr_safesound(PlayerHurt);
            }

            if (MainApple.x - x > 340)
                hspeed = chaseSpeed + 7;
            else
                hspeed = chaseSpeed;

            break;


        case ("Left"):
            if (MainApple.x > x)
            {
                lives = 0;
                MainApple.alarm[0]=30;
                scr_safesound(PlayerHurt);
            }

            if (x - MainApple.x > 340)
                hspeed = chaseSpeed - 7;
            else
                hspeed = chaseSpeed;

            break;


        case ("Up"):
            if (MainApple.y > y)
            {
                lives = 0;
                MainApple.alarm[0]=30;
                scr_safesound(PlayerHurt);
            }

            if (y - MainApple.y > 340)
                vspeed = -chaseSpeed - 7;
            else
                vspeed = chaseSpeed;

            break;


        case ("Down"):
            if (MainApple.y < y)
            {
                lives = 0;
                MainApple.alarm[0]=30;
                scr_safesound(PlayerHurt);
            }

            if (MainApple.y - y > 340)
                vspeed = chaseSpeed + 7;
            else
                vspeed = chaseSpeed;

            break;
    }
}
