arguments.argument:
wallslide = "none";
if not place_meeting(x, y+1, Jello)
{
    pinkrofljumps = 0;
    if instance_number(PinkRoflcopterOrbiter) > 0 
    {
        scr_safesound(BalloonDeflating);
        with (PinkRoflcopterOrbiter)
        {
            instance_change(PinkRoflcopterOrbiterDeflating, true);
        }
    }
}

scr_safesound(PlayerDie);

if instance_number(PinkRoflcopterOrbiter) > 0 
{
    scr_safesound(BalloonDeflating);
    with (PinkRoflcopterOrbiter)
    {
        instance_change(PinkRoflcopterOrbiterDeflating, true);
    }
    with (PinkRoflcopterOrbiterConsumed)
    {
        instance_change(PinkRoflcopterOrbiterDeflating, true);
    }
}

// Beer
beerspeed = 15
if place_meeting(x,y,Beer) and beerdirection!=-1
{
    if beerdirection != beerdirectionlast
    {
        scr_safesound(IStoleThis);
    }
    beerdirectionlast = beerdirection;

    repeat beerspeed
    {
        goalx = x + lengthdir_x(1,beerdirection);
        goaly = y + lengthdir_y(1,beerdirection);
        if place_free(goalx,goaly)
        {
            x = goalx;
            y = goaly;
        }
        else
        {
            while (place_meeting(x + lengthdir_x(-32,beerdirection), y + lengthdir_y(-32,beerdirection), Beer))
            {
                x += lengthdir_x(-32,beerdirection);
                y += lengthdir_y(-32,beerdirection);
            }
            scr_safesound(Spawn);
        }
        vspeed = 0;
        gravity = 0;
        dsmash = 0;
    }
}
else
{
    if beerdirection != -1
    {
        vspeed = lengthdir_y(beerspeed,beerdirection);
    }
    beerdirection = -1;
    beerdirectionlast = -1;
}

// Pink Rofl
if pinkrofljumps > 0
{
    vspeed -= 0.3*power(pinkrofljumps/5, 0.3);
    if dsmash = 0
    {
        vspeed = min(vspeed, (5-pinkrofljumps) * 2 + 2);
    }
    // part_particles_create(pinkparticlesystem,x+8,y+8,pinkparticletype,round(pinkrofljumps/5*4));
    // pinkrofljumps -= 1/60;
}
if pinkrofljumps < 0
{
    pinkrofljumps = 0;
}

// Wall Jumping
if wallslide != "none"
{
    if ((!place_meeting(x+1, y, BasicWall) and !place_meeting(x-1, y, BasicWall)) or slimebuildup=0) and !place_meeting(x+1, y, StickyWall) and !place_meeting(x-1, y, StickyWall)
    {
        wallslide = "none";
    } 
}
if wallslide != "none"
{
    vspeed = 2;
    part_type_direction(slimeparticletype,225,315,0,0);
    part_type_speed(slimeparticletype,0.8,1.4,0,0);
    if wallslide = "right"
    {
        part_particles_create(slimeparticlesystem,x+32,y+24,slimeparticletype,3);
    }
    else if wallslide = "left"
    {
        part_particles_create(slimeparticlesystem,x,y+24,slimeparticletype,3);
    }
}
force = 0;
if wallforce = "left"
{
    force = 4;
}
else if wallforce = "right"
{
    force = -4;
}
if place_free(x+force, y)
{
    x+=force;
}

if vspeed >= -6
{
    wallforce = "none";
}

// Global timer
global.timer += 1;

if air = 0 && x != xprevious && (keyboard_check(vk_left) or keyboard_check(vk_right)) && global.cheat_APP_TROTS = 1
{movementTimer += 1;}
else movementTimer = 0;

if hspeed > 8 hspeed = 8;
if hspeed < -8 hspeed = -8;
