arguments.argument:
//if not place_meeting(x,y-1,MainApple) && not place_meeting(mateID.x,mateID.y-1,MainApple) && not place_meeting(x,y+4,MainApple) && not place_meeting(mateID.x,mateID.y+4,MainApple)

if !variable_local_exists("mateID")
   exit;


if (mateFound)
{
with(MainApple)
{
    if not place_meeting(x,y+vspeed+other.tolerance,BasicWall) other.halfwall = 0;
    else other.halfwall = 1;
}

if not collision_rectangle(x,y-4,x+32,y+36,MainApple,true,false) && not collision_rectangle(mateID.x,mateID.y-4,mateID.x+32,mateID.y+36,MainApple,true,false)
{
    if yrl < yi yrl += 1;
    if yrl > yi yrl -= 1;
}

if not instance_exists(MainApple) exit;

if place_free(xrl,yrl+tolerance) && MainApple.air = 0
{
    with(mateID) if place_free(xrl,yrl-tolerance)
    {
        with(mateID)
        {
            if place_meeting(x,y-1,MainApple)
            {
                yrl += tolerance;
                mateID.yrl -= tolerance;
            }
            if place_meeting(x,y-1,MainApple) && halfwall = 0 with(MainApple) y += other.tolerance;
        }
    }
}

x = xrl;
y = yrl;
}
