arguments.argument:
isUsed = true;
sprite_index = spr_UsedBubble;    
alarm[1] = 60;

if (instance_number(MainApple) < 0)
    exit;

removeGravity = true;       
alarm[2] = 10;

if (keyboard_check(vk_right))
{
    MainApple.isWalkable = true;
    MainApple.vspeed = -1;
    MainApple.hspeed = 13;
}

if (keyboard_check(vk_left))
{
    MainApple.isWalkable = true;
    MainApple.vspeed = -1;
    MainApple.hspeed = -13;
}

if (keyboard_check(vk_up))
{
    MainApple.isWalkable = true;
    MainApple.vspeed = -20;
}

if (keyboard_check(vk_down))
{
    MainApple.isWalkable = true;
    MainApple.vspeed = 20;
}

MainApple.isWalkable = true;
