arguments.argument:
while (not place_meeting(x+1, y, Stop)) {
    image_xscale += 1;
}

while (not place_meeting(x, y+1, Stop)) {
    image_yscale += 1;
}

for (xx=0; xx<image_xscale; xx+=fineness)
{
    for (yy=0; yy<image_yscale; yy+=fineness)
    {
        instance_create(x+32*xx, y+32*yy, PaperSkin);
    }
}

instance_destroy();
