arguments.argument:
with (BasicWall) {
    above=false;
    below=false;
    right=false;
    left=false;
    topleft=false;
    topright=false;
    bottomleft=false;
    bottomright=false;
    if place_meeting(x,y-32,BasicWall) {above=true};
    if place_meeting(x,y+32,BasicWall) {below=true};
    if place_meeting(x+32,y,BasicWall) {right=true};
    if place_meeting(x-32,y,BasicWall) {left=true};
    if place_meeting(x-32,y-32,BasicWall) {topleft=true};
    if place_meeting(x+32,y-32,BasicWall) {topright=true};
    if place_meeting(x-32,y+32,BasicWall) {bottomleft=true};
    if place_meeting(x+32,y+32,BasicWall) {bottomright=true};
    ///Sides
    if above=false {tile_add(WallSidesBrass,0,0,32,32,x,y,80)};
    if right=false {tile_add(WallSidesBrass,32,0,32,32,x,y,80)};
    if below=false {tile_add(WallSidesBrass,64,0,32,32,x,y,80)};
    if left=false {tile_add(WallSidesBrass,96,0,32,32,x,y,80)};
    ///Outer Corners
    if above=false && left=false {tile_add(WallSidesBrass,0,32,32,32,x,y,80)};
    if above=false && right=false {tile_add(WallSidesBrass,32,32,32,32,x,y,80)};
    if below=false && right=false {tile_add(WallSidesBrass,64,32,32,32,x,y,80)};
    if below=false && left=false {tile_add(WallSidesBrass,96,32,32,32,x,y,80)};
    ///Inner Corners
    if above=true && left=true && topleft=false {tile_add(WallSidesBrass,0,64,32,32,x,y,80)};
    if above=true && right=true && topright=false {tile_add(WallSidesBrass,32,64,32,32,x,y,80)};
    if below=true && right=true && bottomright=false {tile_add(WallSidesBrass,64,64,32,32,x,y,80)};
    if below=true && left=true && bottomleft=false {tile_add(WallSidesBrass,96,64,32,32,x,y,80)};
    ///Non-Corners
    if above=false && right=true {tile_add(WallSidesBrass,0,96,32,32,x,y,80)};
    if above=false && left=true {tile_add(WallSidesBrass,32,96,32,32,x,y,80)};
    if left=false && below=true {tile_add(WallSidesBrass,64,96,32,32,x,y,80)};
    if left=false && above=true {tile_add(WallSidesBrass,96,96,32,32,x,y,80)};
    if right=false && below=true {tile_add(WallSidesBrass,0,128,32,32,x,y,80)};
    if right=false && above=true {tile_add(WallSidesBrass,32,128,32,32,x,y,80)};
    if below=false && left=true {tile_add(WallSidesBrass,64,128,32,32,x,y,80)};
    if below=false && right=true {tile_add(WallSidesBrass,96,128,32,32,x,y,80)};
}
instance_destroy();
