{
    image_angle=90;
}
else if scr_place_meeting_any_wall(x, y-2)
{
    image_angle=270;
}
else if scr_place_meeting_any_wall(x-2, y)
{
    image_angle=0;
}
else if scr_place_meeting_any_wall(x+2, y)
{
    image_angle=180;
}
